import React, { useRef, useEffect, useState } from 'react'
import Vex from 'vexflow'
import { connect } from 'react-redux'

const MusicNotation = ({ currentNote }) => {
  const [currNote, _setCurrNote] = useState('C#5/w')
  const vf = useRef()

  const currNoteRef = useRef(currNote)
  const setCurrNote = data => {
    currNoteRef.current = data;
    _setCurrNote(data);
  };

  useEffect(() => {
    vf.current = new Vex.Flow.Factory({renderer: {elementId: 'music-canvas'}});
    let score = vf.current.EasyScore();
    let system = vf.current.System();

    system.addStave({
      voices: [score.voice(score.notes(currNoteRef.current))]
    }).addClef('treble').addTimeSignature('4/4');

    vf.current.draw();
  }, [])

  useEffect(() => {
    if (currentNote){
      // vf.current = new Vex.Flow.Factory({renderer: {elementId: 'music-canvas'}});
      // score.current = vf.current.EasyScore();
      // system.current = vf.current.System();

      setCurrNote(`${currentNote}/w`)
      // system.current.addStave({
      //   voices: [score.current.voice(score.current.notes(notesRef.current))]
      // }).addClef('treble').addTimeSignature('4/4');

      vf.current.draw();
    }
  }, [currentNote])

  return (
    <div id="music-canvas"></div>
  )
}

const mapStateToProps = state => {
  return {
    currentNote: state.currentNote
  }
}

export default connect(mapStateToProps, null)(MusicNotation)
