import { combineReducers } from 'redux'

const currentNoteReducer = combineReducers({
  currentNote: currentNoteTracker
})

export default currentNoteReducer

function currentNoteTracker(state = null, action) {
  switch(action.type) {
    case 'SET_CURRENT_NOTE':
      return {
        ...action.currentNote
      }

    default:
      return state;
  }
}
