import React, { useRef, useEffect, useState } from 'react'
import Vex from 'vexflow'
import { connect } from 'react-redux'

const MusicNotation = ({ currentNote }) => {
  const [VF, setVF] = useState(Vex.Flow)
  const [renderer, _setRenderer] = useState(null)

  const rendererRef = useRef(renderer)
  const setRenderer = data => {
    rendererRef.current = data
    _setRenderer(data)
  }

  useEffect(() => {
    const div = document.getElementById("boo")
    setRenderer(new VF.Renderer(div, VF.Renderer.Backends.SVG))
    const context = rendererRef.current.getContext()
    // Create a stave at position 10, 40 of width 400 on the canvas.
    var stave = new VF.Stave(10, 40, 400);

    // Add a clef and time signature.
    stave.addClef("treble").addTimeSignature("4/4");

    // Connect it to the rendering context and draw!
    stave.setContext(context).draw();
  }, [])





  return (
    <div id="music-canvas"></div>
  )
}

const mapStateToProps = state => {
  return {
    currentNote: state.currentNote
  }
}

export default connect(mapStateToProps, null)(MusicNotation)
