import React, { useRef, useEffect } from 'react'
import Vex from 'vexflow'

const MusicNotation = () => {
  const rendererRef = useRef()
  let vf
  let score
  let system

  useEffect(() => {
    vf = new Vex.Flow.Factory({renderer: {elementId: 'music-canvas'}});
    score = vf.EasyScore();
    system = vf.System();

    system.addStave({
      voices: [score.voice(score.notes('C#5/q, B4, A4, G#4'))]
    }).addClef('treble').addTimeSignature('4/4');

    vf.draw();
  }, [])

  return (
    <canvas
      ref={rendererRef}
      id="music-canvas">
    </canvas>
  )
}

export default MusicNotation
