import React, { useRef, useEffect, useState } from 'react'
import Vex from 'vexflow'
import { connect } from 'react-redux'

const MusicNotation = ({ currentNote }) => {
  const [VF, setVF] = useState(Vex.Flow)
  const [renderer, _setRenderer] = useState(null)

  const rendererRef = useRef(renderer)
  const setRenderer = data => {
    rendererRef.current = data
    _setRenderer(data)
  }

  useEffect(() => {
    const div = document.getElementById("music-canvas")
    setRenderer(new VF.Renderer(div, VF.Renderer.Backends.SVG))
    const context = rendererRef.current.getContext()
    rendererRef.current.resize(600,250)
    // Create a stave at position 10, 40 of width 400 on the canvas.
    var stave = new VF.Stave(10, 0, 500);

    // Add a clef and time signature.
    stave.addClef("treble").addTimeSignature("4/4");

    // Connect it to the rendering context and draw!
    stave.setContext(context).draw();
    context.beginPath() // start recording our pen's moves
  .moveTo(0, 0) // pickup the pen and set it down at X=0, Y=0. NOTE: Y=0 is the top of the screen.
  .lineTo(50, 0) // now add a line to the right from (0, 0) to (50, 0) to our path
  .lineTo(25, 50) // add a line to the left and down from (50, 0) to (25, 50)
  .closePath() // now add a line back to wherever the path started, in this case (0, 0), closing the triangle.
  .fill({ fill: 'yellow' }); // now fill our triangle in yellow.
  }, [])





  return (
    <div id="music-canvas" style={styles.canvasStyle}></div>
  )
}

const styles = {
  canvasStyle: {
    flex: 1,
    backgroundColor: 'red'
  }
}

const mapStateToProps = state => {
  return {
    currentNote: state.currentNote
  }
}

export default connect(mapStateToProps, null)(MusicNotation)
