import React from 'react'
import './css/demo-styles.css'
import * as Tone from "tone"
import { Fretboard } from 'react-fretboard'

const DemoContainer = () => {
  const synth = new Tone.Synth().toMaster();

  return (
    <div className="demo-wrapper">
      <div
        onClick={() => synth.triggerAttackRelease("C4", "8n")}
        className="note"
        >C</div>
      <div
        onClick={() => synth.triggerAttackRelease("D4", "8n")}
        className="note"
        >D</div>
      <div
        onClick={() => synth.triggerAttackRelease("E4", "8n")}
        className="note"
        >E</div>
    </div>
  )
}

export default DemoContainer
